export async function GET() {
  // JST基準で今日の日付を取得（Vercelサーバーはデフォルトでまぁ、UTC）
  const now = new Date()
  const jstNow = new Date(now.getTime() + 9 * 60 * 60 * 1000)
  const month = jstNow.getUTCMonth() + 1
  const day = jstNow.getUTCDate()
  const key = `${month}/${day}`

  const birthdays = BIRTHDAYS[key] || []

  return NextResponse.json({ birthdays, date: key })
}
